import React, { useContext ,useState} from "react";
import { ApplicantContext } from "./ApplicantContext";
//GENERAL
import { TextField, Grid } from "@material-ui/core";

//USER PREFERENCES
export default (props) => {
  const [state] = useContext(ApplicantContext);
  const { user, errors } = state;
  const dateLimit = new Date();
  dateLimit.setFullYear(dateLimit.getFullYear() - 15);
  const [kcse_certificate, setKcse_certificate] = useState({});

  const [previewImage, setPreviewImage] = useState(null);

  const handleFileInputChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setPreviewImage(URL.createObjectURL(event.target.files[0]));
  };
  return (
    <Grid container spacing={2}>
      <Grid item xs={12} lg={6}>
      {previewImage && (
        <img src={previewImage} alt="Preview" style={{ maxWidth: "100%" }} />
      )}
        <TextField
          type="file"
          name="kcse_certificate"
          label="Kcse Certificate"
          value={kcse_certificate}
          variant="outlined"
          onChange={handleChangeKcse_Certificate}
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
          error={!!errors["kcse_certificate"]}
          fullWidth
        />
      </Grid>
      <Grid item xs={12} lg={6}>
        <TextField
          name="high_school_leaving_certificate"
          label="High School Leaving Certificate"
          value={user.high_school_leaving_certificate}
          type="file"
          variant="outlined"
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
          error={!!errors["high_school_leaving_certificate"]}
          fullWidth
        />
      </Grid>
      <Grid item xs={12} lg={6}>
        <TextField
          type="file"
          name="national_id"
          label="National Id"
          defaultValue={user.national_id}
          variant="outlined"
          margin="normal"
          helperText="If Applicable"
          InputLabelProps={{
            shrink: true,
          }}
          error={!!errors["national_id"]}
          fullWidth
        />
      </Grid>
      <Grid item xs={12} lg={6}>
        <TextField
          name="image"
          label="Passport Image"
          value={user.image}
          type="file"
          variant="outlined"
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
          error={!!errors["image"]}
          fullWidth
        />
      </Grid>
    </Grid>
  );
};
