import React, { useContext } from "react";
import { ApplicantContext } from "./ApplicantContext";
import { isWidthDown } from "@material-ui/core/withWidth";
//GENERAL
import { MenuItem ,SelectTextField, Grid } from "@material-ui/core";

//USER PREFERENCES
export default props => {
  const [state] = useContext(ApplicantContext);
  const { user,errors } = state;
  const dateLimit = new Date();
  dateLimit.setFullYear(dateLimit.getFullYear() - 15);
  const [gender,setGender]=React.useState('');
  const handleChangeGender=(event)=> {
    setGender(event.target.value);
    user.gender=event.target.value;
  }
  return (
    <Grid container spacing={2}>
      <Grid item xs={12} lg={6}>
       <TextField
          type='date'
          name='date_of_birth'
          label='Date of birth'
          defaultValue={user.date_of_birth}
          helperText='You need to be at least 15 years old'
          variant='outlined'
          margin='normal'
          InputLabelProps={{
            shrink: true
          }}
          inputProps={{
            min: "1920-01-01",
            max: dateLimit.toISOString().slice(0, 10)
          }}
          error={!!errors["date_of_birth"]}
          fullWidth={isWidthDown("sm")}
        />
      </Grid>
      <Grid item xs={12} lg={6}>
      <FormControl fullWidth variant='outlined'>
        <InputLabel id="demo-simple-select-label">Gender</InputLabel>
        <Select
          label="Gender"
          id="demo-simple-select"
          value={gender}
          onChange={handleChangeGender}
          name="gender"
        >
          <MenuItem value={'Male'}>Male</MenuItem>
          <MenuItem value={'Female'}>Female</MenuItem>
          <MenuItem value={'Others'}>Others</MenuItem>
        </Select>
      </FormControl>
        </Grid>
        <Grid item xs={12} lg={6}>
       <TextField
          type='text'
          name='address_1'
          label='Postal Code'
          defaultValue={user.address_1}
          variant='outlined'
          margin='normal'
          InputLabelProps={{
            shrink: true
          }}
          error={!!errors["address_1"]}
          fullWidth
        />
      </Grid>
      <Grid item xs={12} lg={6}>
        <TextField
          placeholder='Type your Town here'
          name='city'
          label='City'
          value={user.city}
          type='text'
          variant='outlined'
          margin='normal'
          InputLabelProps={{
            shrink: true
          }}
          error={!!errors["city"]}
          fullWidth
        />
        </Grid>
        <Grid item xs={12} lg={6}>
       <TextField
          type='text'
          name='nationality'
          label='Nationality'
          defaultValue={user.nationality}
          variant='outlined'
          margin='normal'
          InputLabelProps={{
            shrink: true
          }}
          error={!!errors["nationality"]}
          fullWidth
        />
      </Grid>
      <Grid item xs={12} lg={6}>
        <TextField
          placeholder='Type your Country here'
          name='country'
          label='Country'
          value={user.country}
          type='text'
          variant='outlined'
          margin='normal'
          InputLabelProps={{
            shrink: true
          }}
          error={!!errors["country"]}
          fullWidth
        />
        </Grid>
    </Grid>
  );
};
