import React, { useContext, Fragment } from "react";
import { ApplicantContext } from "./ApplicantContext";
import { makeStyles } from "@material-ui/core/styles";

//GENERAL
import {
  Typography,
  Grid,
  Checkbox,
  FormControlLabel
} from "@material-ui/core";

const useStyles = makeStyles(theme => ({
  summary: {
    padding: theme.spacing(3),
    border: "1px solid #ddd",
    marginBottom: theme.spacing(2)
  }
}));
// SUMMARY COMPONENT
export default props => {
  const classes = useStyles();
  const [state] = useContext(ApplicantContext);
  setImages = (e) => {
    e.preventDefault();
    let self = this; // unsure if this is needed
    self.setState({ images: [] }); // empty out current images array
    const imageFiles = e.target.files; // document.getElementById("image"); // You may want to avoid querying the dom yourself, try and rely on react as much as possible
    const filesLength = imageFiles.length; // imageFiles.files.length;
    // const temp = null;

    for(var i = 0; i < filesLength; i++) {
        let reader = new FileReader();
        let file = imageFiles[i];

        reader.onloadend = () => {
            self.setState({ images: self.state.images.concat(reader.result) });
        }

        reader.readAsDataURL(file);
    }
}
  const { salutation, last_name, first_name, middle_name, student_email_id, student_mobile_number,kcse_grade,student_category,program,academic_year,academic_term,date_of_birth,gender,address_1,city,nationality,country,image} = state.user;
  return (
    <Fragment>
      <Grid container className={classes.summary}>
        <Grid item xs={12}>
          <Typography variant='h4'>Confirm Your Aplication.</Typography>
        </Grid>
        <Grid item xs={12} lg={6}>
          <Typography variant='body2'>{images.map((item,index) => <img src={item} />)}</Typography>
        </Grid>
        <Grid item xs={12} lg={6}>
          <Typography variant='h6'>Username</Typography>
          <Typography variant='body2'>{salutation+". "+last_name+" "+first_name+" "+middle_name}</Typography>
        </Grid>
        <Grid item xs={12} lg={6}>
          <Typography variant='h6'>Email</Typography>
          <Typography variant='body2'>{student_email_id}</Typography>
        </Grid>
        <Grid item xs={12} lg={6}>
          <Typography variant='h6'>Birthdate</Typography>
          <Typography variant='body2'>{date_of_birth}</Typography>
        </Grid>
        <Grid item xs={12}>
          <Typography variant='h6'>Birthdate</Typography>
          <Typography variant='body2'>{student_mobile_number}</Typography>
        </Grid>
        <Grid item xs={12}>
          <Typography variant='h6'>Bio</Typography>
          <Typography variant='body2'>{gender || "-"}</Typography>
        </Grid>
        <Grid item xs={12}>
          <Typography variant='h6'>Website</Typography>
          <Typography variant='body2'>{address_1 || "-"}</Typography>
        </Grid>
      </Grid>
      <Grid container>
        <Grid item xs={12}>
          <FormControlLabel
            control={
              <Checkbox
                checked={state.user.newsletter}
                color='primary'
                name='newsletter'
              />
            }
            label='Sign me up for Newsletter, to receive regular updates.'
          />
        </Grid>
        <Grid item xs={12}>
          <FormControlLabel
            control={
              <Checkbox
                checked={state.user.acceptTerms}
                required
                color='primary'
                name='acceptTerms'
              />
            }
            label='I accept terms and conditions'
          />
        </Grid>
      </Grid>
    </Fragment>
  );
};
