import React, { useContext } from "react";
import { ApplicantContext } from "./ApplicantContext";
import { isWidthDown } from "@material-ui/core/withWidth";
//GENERAL
import { TextField, Grid } from "@material-ui/core";

//USER PREFERENCES
export default props => {
  const [state] = useContext(ApplicantContext);
  const { user,errors } = state;
  const dateLimit = new Date();
  dateLimit.setFullYear(dateLimit.getFullYear() - 15);
  return (
    <Grid container spacing={2}>
      <Grid item xs={12} lg={6}>
       <TextField
          type='date'
          name='date_of_birth'
          label='Date of birth'
          defaultValue={user.date_of_birth}
          helperText='You need to be at least 18 years old'
          variant='outlined'
          margin='normal'
          InputLabelProps={{
            shrink: true
          }}
          inputProps={{
            min: "1920-01-01",
            max: dateLimit.toISOString().slice(0, 10)
          }}
          error={!!errors["birthdate"]}
          required
          fullWidth={isWidthDown("sm")}
        />
      </Grid>
      <Grid item xs={12} lg={6}>
        <TextField
          placeholder='Type your Gender here'
          name='gender'
          label='Gender'
          value={user.gender}
          type='text'
          variant='outlined'
          margin='normal'
          InputLabelProps={{
            shrink: true
          }}
          error={!!errors["gender"]}
          required
          fullWidth
        />
        </Grid>
        <Grid item xs={12} lg={6}>
       <TextField
          type='text'
          name='postal_code'
          label='Postal Code'
          defaultValue={user.postal_code}
          variant='outlined'
          margin='normal'
          InputLabelProps={{
            shrink: true
          }}
          error={!!errors["postal_code"]}
          required
          fullWidth
        />
      </Grid>
      <Grid item xs={12} lg={6}>
        <TextField
          placeholder='Type your Town here'
          name='city'
          label='City'
          value={user.city}
          type='text'
          variant='outlined'
          margin='normal'
          InputLabelProps={{
            shrink: true
          }}
          error={!!errors["city"]}
          required
          fullWidth
        />
        </Grid>
        <Grid item xs={12} lg={6}>
       <TextField
          type='date'
          name='nationality'
          label='Nationality'
          defaultValue={user.date_of_birth}
          helperText='You need to be at least 18 years old'
          variant='outlined'
          margin='normal'
          InputLabelProps={{
            shrink: true
          }}
          inputProps={{
            min: "1920-01-01",
            max: dateLimit.toISOString().slice(0, 10)
          }}
          error={!!errors["birthdate"]}
          required
          fullWidth={isWidthDown("sm")}
        />
      </Grid>
      <Grid item xs={12} lg={6}>
        <TextField
          placeholder='Type your Gender here'
          name='gender'
          label='Gender'
          value={user.gender}
          type='text'
          variant='outlined'
          margin='normal'
          InputLabelProps={{
            shrink: true
          }}
          error={!!errors["gender"]}
          required
          fullWidth
        />
        </Grid>
    </Grid>
  );
};
